frappe.ui.form.on('Batch', {
    item: function(frm) {
        if (frm.is_new() && frm.doc.item && !frm.doc.batch_id) {
            frappe.call({
                method: 'mohan_impex.batch_api.get_next_batch_id',
                args: {
                    item: frm.doc.item
                },
                callback: function(r) {
                    if (r.message && r.message.next_batch_id) {
                        frm.set_value('batch_id', r.message.next_batch_id);
                    }
                }
            });
        }
    }
});

