frappe.ui.form.on('Transport RFQ', {
    onload: function(frm) {
        frappe.call({
            method: "mohan_impex.user_roles.transporter_user",
            callback: function(r) {
                if (r.message) {
                    // Check if the row already exists
                    let existing_rows = frm.doc.transporter_details.filter(row => row.transporter === r.message);

                    if (existing_rows.length === 1 && frm.doc.transporter_details.length === 1) {
                        // Don't touch the form if it's already set up properly
                        return;
                    }

                    // Replace with only the matching row (or add it if missing)
                    frm.clear_table("transporter_details");

                    let row = frm.add_child("transporter_details");

                    if (existing_rows.length) {
                        Object.assign(row, existing_rows[0]); // copy data if already exists
                    } else {
                        row.transporter = r.message; // set if new
                    }

                    frm.refresh_field("transporter_details");
                }
            }
        });
    }
});

frappe.ui.form.on('Transporters', {
    quoted_amount: calculate_actual_cost,
    slab: calculate_actual_cost
});

function calculate_actual_cost(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    let total_weight = flt(frm.doc.total_weightin_kg) || 0;
    let quoted = flt(row.quoted_amount);
    let actual_cost = 0;

    if (row.slab === "Per KG") {
        actual_cost = quoted * total_weight;
    } else if (row.slab === "Per Ton") {
        actual_cost = (quoted * total_weight) / 1000;
    } else if (row.slab === "Total Frieght") {
        actual_cost = quoted;
    }

    frappe.model.set_value(cdt, cdn, 'actual_cost', actual_cost);
}

frappe.ui.form.on('Supplier Quotation', {
    onload: function(frm) {
        let allowedRoles = [
            "Administrator", 
            "Purchase Executive", 
            "Purchase Manager", 
            "Purchase Master Manager", 
            "Purchase User", 
            "SCM Manager"
        ];

        let hasAccess = frappe.user_roles.some(role => allowedRoles.includes(role));

        // Only clear if it's a new doc AND user is unauthorized
        if (!hasAccess && frm.is_new()) {
            frm.doc.items.forEach(item => {
                item.rate = 0;
                item.amount = 0;
            });
            frm.refresh_field('items');
        }
    }
});


frappe.ui.form.on("Supplier Quotation", "validate", function(frm) {
	const today = frappe.datetime.get_today();

	if (frm.doc.transaction_date < today) {
		frappe.msgprint(__("Date cannot be in the past."));
		frappe.validated = false;
	}

	if (frm.doc.valid_till && frm.doc.valid_till < today) {
		frappe.msgprint(__("Valid Till Date cannot be in the past."));
		frappe.validated = false;
	}
});

