frappe.ui.form.on("Sales Order", {
    customer: function(frm) {
        // Reset custom_outstanding_amount only
        frm.set_value("custom_outstanding_amount", 0);

        if (frm.doc.company && frm.doc.customer) {
            frappe.call({
                method: "erpnext.accounts.utils.get_balance_on",
                args: {
                    party_type: "Customer",
                    party: frm.doc.customer,
                    company: frm.doc.company
                },
                callback: function(r) {
                    if (!r.exc && r.message != null) {
                        const balance = r.message;

                        frm.set_value("custom_outstanding_amount", balance);

                        frm.refresh_field("custom_outstanding_amount");
                    }
                }
            });
        }
    },

