        // No action needed since approval field is removed
    },

    before_save: function(frm) {
        // Clean up any unwanted fields from child table
        frm.doc.items.forEach(row => {
            if (row.available_quantity && typeof row.available_quantity !== "number") {
                row.available_quantity = 0;
            }
        });
    }
});

// Update available quantity safely from backend response
frappe.ui.form.on('Sales Order Item', {
    item_code: function(frm, cdt, cdn) {
        update_available_quantity(frm, cdt, cdn);
    },
    warehouse: function(frm, cdt, cdn) {
        update_available_quantity(frm, cdt, cdn);
    }
});

function update_available_quantity(frm, cdt, cdn) {
    let row = locals[cdt][cdn];

    if (row.item_code && row.warehouse) {
        frappe.call({
            method: "mohan_impex.Sales.available_stock.get_item_stock",
            args: {
                item_code: row.item_code,
                warehouse: row.warehouse
            },
            callback: function(r) {
                if (r.message && typeof r.message === "object" && "actual_qty" in r.message) {
                    frappe.model.set_value(cdt, cdn, "available_quantity", r.message.actual_qty);
                } else {
                    frappe.model.set_value(cdt, cdn, "available_quantity", 0);
                }
            }
        });
    }
}

frappe.ui.form.on('Pick List', {
    refresh: function(frm) {
        if (!frm.doc.__islocal && frm.doc.docstatus === 1) {
            frm.add_custom_button(__('Create Transport RFQ'), function() {
                frappe.model.with_doctype('Transport RFQ', function() {
                    let new_doc = frappe.model.get_new_doc('Transport RFQ');
                    new_doc.pick_list = frm.doc.name;
                    new_doc.from_location = frm.doc.parent_warehouse;
                    new_doc.to_location = frm.doc.to_warehouse || '';
                    new_doc.transport_date = frappe.datetime.now_date();

                    let total_weight = 0;

                    // Copy data from Pick List's locations table
                    (frm.doc.locations || []).forEach((row) => {
                        let child = frappe.model.add_child(new_doc, 'Transport RFQ Item', 'locations');
                        child.item_code = row.item_code;
                        child.qty = row.qty;
                        child.uom = row.uom;
                        child.weightin_kg = row.stock_qty;

                        // Sum up the weights
                        total_weight += flt(row.stock_qty);
                    });

                    // Set total_weightin_kg
                    new_doc.total_weightin_kg = total_weight;

                    frappe.set_route('Form', 'Transport RFQ', new_doc.name);
                });
            }, __('Create'));
        }

        set_item_code_filter(frm);
    }
});


