frappe.ui.form.on('Pick List', {
    onload: function(frm) {
        if (!frm.doc.custom_shipping_address && frm.doc.locations?.length > 0) {
            const so_name = frm.doc.locations[0].sales_order;

            if (so_name) {
                frappe.call({
                    method: 'frappe.client.get',
                    args: {
                        doctype: 'Sales Order',
                        name: so_name
                    },
                    callback: function(r) {
                        if (r.message && r.message.shipping_address) {
                            const clean_address = stripHtmlAddress(r.message.shipping_address);
                            frm.set_value('custom_shipping_address', clean_address);
                        }
                    }
                });
            }
        }
    }
});

function stripHtmlAddress(html) {
    return html.replace(/<br\s*\/?>/gi, '\n').trim();
}

