});

function apply_address_filter(frm) {
    let filters = {
        is_your_company_address: 1,
        link_doctype: "Company"
    };

    let company_name = frm.doc.company ? frm.doc.company : "empty"; // Handle empty company case

    if (frm.doc.company) {
        filters["link_name"] = frm.doc.company;
    } else {
        filters["link_name"] = ""; // Ensures empty filter when no company is selected
    }

    frm.set_query("custom_company_shipping_address", function() {
        return {
            filters: filters
        };
    });

    // Display applied filter as a message in the form
    let filter_message = `Filters applied for <b>Is Your Company Address</b> = Yes, <b>Link Doctype</b> = Company, <b>Link Name</b> = ${company_name}`;

    // Show message in a section break instead of a pop-up
    frm.fields_dict['custom_filter_message']?.$wrapper.html(`<div style="padding: 10px; background-color: #f5f5f5; border-radius: 5px;">${filter_message}</div>`);
}

// Ensure there's a section in the form to show the filter message
frappe.ui.form.on('Request for Quotation', {
