        } else {
            frm.set_value("custom_shipping_address_details", ""); // Clear if address is not selected
        }

        set_item_code_filter(frm);
    },

    refresh: function (frm) {
        set_item_code_filter(frm);
    }
});

// Fetch shipping address details and set formatted address
function fetch_address_details(frm) {
    frappe.call({
        method: "frappe.client.get",
        args: {
            doctype: "Address",
            name: frm.doc.custom_company_shipping_address
        },
        callback: function (r) {
            if (r.message) {
                const addr = r.message;
                const formatted_address = [
                    addr.address_line1,
                    addr.address_line2,
                    addr.city,
                    addr.state,
                    addr.pincode,
                    addr.country
                ].filter(Boolean).join("\n"); // Remove empty lines and format

                frm.set_value("custom_shipping_address_details", formatted_address);
            } else {
                frm.set_value("custom_shipping_address_details", "");
            }
        }
    });
}

