// ===== Parent: Advance Supplier Notification =====
frappe.ui.form.on('Advance Supplier Notification', {
    // Keep total_qty always correct in UI
    refresh(frm) { recompute_total_qty(frm); },
    items_add(frm) { recompute_total_qty(frm); },
    items_remove(frm) { recompute_total_qty(frm); },

    validate(frm) {
        recompute_total_qty(frm);

        const today = frappe.datetime.get_today();
        if (frm.doc.invoice_date && frm.doc.invoice_date < today) {
            frappe.msgprint(__("Invoice Date cannot be in the past."));
            frm.validated = false;
        }
    },

    before_save(frm) {
        recompute_total_qty(frm);
    },

    //  Run updates only when ASN is submitted
    on_submit: async function (frm) {
        try {
            if (!frm.doc.purchase_order) return;

            // 1) Compute cumulative total ASN qty across ALL ASNs for this PO
            const asn_list = await frappe.db.get_list('Advance Supplier Notification', {
                filters: { purchase_order: frm.doc.purchase_order },
                fields: ['name'],
                limit: 5000
            });

            let cumulative_total_qtyasn = 0;
            for (const a of asn_list) {
                const asn_doc = await frappe.db.get_doc('Advance Supplier Notification', a.name);
                (asn_doc.items || []).forEach(row => {
                    cumulative_total_qtyasn += (row.qty || 0);
                });
            }

            // 2) Fetch the PO and cap header cumulative at ordered total
            const po = await frappe.db.get_doc('Purchase Order', frm.doc.purchase_order);
            const ordered_total_qty = (po.items || []).reduce((s, it) => s + (it.qty || 0), 0);
            const capped_total_qtyasn = Math.min(cumulative_total_qtyasn, ordered_total_qty);

            // 3) Map THIS ASN's items to add line-wise
            const qty_by_item_from_this_asn = {};
            (frm.doc.items || []).forEach(row => {
                const code = row.item;     // ASN child field: item
                const qty  = row.qty || 0; // integers in your setup
                if (!code) return;
                qty_by_item_from_this_asn[code] = (qty_by_item_from_this_asn[code] || 0) + qty;
            });

            // 4) Update PO child rows: add this ASN qty to existing custom_asn_qty
            (po.items || []).forEach(it => {
                const code = it.item_code; // PO child field: item_code
                if (qty_by_item_from_this_asn[code]) {
                    it.custom_asn_qty = (it.custom_asn_qty || 0) + qty_by_item_from_this_asn[code];

                    // OPTIONAL per-line cap (enable if desired):
                    // it.custom_asn_qty = Math.min(it.custom_asn_qty, it.qty || 0);
                }
            });

            // 5) Update PO header fields (single save)  NOTE: no status field updates
            po.custom_asn_no                   = frm.doc.name;
            po.custom_purchase_invoice         = frm.doc.purchase_invoice;
            po.custom_invoice_date             = frm.doc.invoice_date;
            po.custom_transporter_name         = frm.doc.transporter_name;
            po.custom_vehiclecontainer_number  = frm.doc.vehiclecontainer_number;
            po.custom_expected_arrival_date_   = frm.doc.expected_arrival_date;
            po.custom_driver_name              = frm.doc.driver_name;
            po.custom_driver_mobile_number     = frm.doc.driver_mobile_number;
            po.custom_total_qtyasn             = capped_total_qtyasn; // capped cumulative header

            await frappe.call({
                method: 'frappe.client.save',
                args: { doc: po }
            });

            frappe.msgprint(__(`PO ${frm.doc.purchase_order} updated  ASN Created (${capped_total_qtyasn}/${ordered_total_qty}).`));
        } catch (e) {
            console.error(e);
            frappe.msgprint({
                title: __("Error"),
                message: __("Failed to update Purchase Order from ASN on submit"),
                indicator: "red"
            });
        }
    }
});

