function recompute_total_qty(frm) {
    let sum = 0;
    (frm.doc.items || []).forEach(r => { sum += (r.qty || 0); });
    if ((frm.doc.total_qty || 0) !== sum) {
        frm.set_value('total_qty', sum);
    }
}

