frappe.ui.form.on('Sales Invoice', {
    onload: function(frm) {
        // Avoid adding transport charges multiple times
        const already_added = frm.doc.taxes.some(tax => tax.description === "Transporter Charges");
        if (already_added) return;

        // Get unique Delivery Notes from items table
        const delivery_notes = [
            ...new Set(
                (frm.doc.items || [])
                    .map(item => item.delivery_note)
                    .filter(dn => dn)
            )
        ];

        if (delivery_notes.length === 0) return;

        // Now fetch each DN and total transport charges
        let total_transport = 0;
        let processed = 0;

        delivery_notes.forEach(dn => {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Delivery Note",
                    name: dn
                },
                callback: function(r) {
                    if (r.message) {
                        let transport = r.message.custom_transport_charges || 0;
                        total_transport += transport;
                    }

                    processed++;
                    if (processed === delivery_notes.length && total_transport > 0) {
                        let row = frm.add_child("taxes");
                        row.charge_type = "Actual";
                        row.account_head = "Sales Expenses - MISL"; // Adjust this
                        row.description = "Transporter Charges";
                        row.tax_amount = total_transport;
                        row.cost_center = "Main - MISL"; // Adjust this too
                        frm.refresh_field("taxes");
                    }
                }
            });
        });
    }
});

