frappe.ui.form.on('Job Card', {
    custom_employee_group: function(frm) {
        if (!frm.doc.custom_employee_group) return;

        // Step 1: Get Employee Template
        frappe.call({
            method: 'frappe.client.get',
            args: {
                doctype: 'Employee Template',
                name: frm.doc.custom_employee_group
            },
            callback: function(r) {
                const log_names = r.message.assign_employee || [];

                frm.clear_table('employee');

                // Step 2: Loop through Job Card Time Log names
                let pending = log_names.length;

                if (pending === 0) {
                    frm.refresh_field('employee');
                }

                log_names.forEach(log_name => {
                    frappe.call({
                        method: 'frappe.client.get',
                        args: {
                            doctype: 'Job Card Time Log',
                            name: log_name
                        },
                        callback: function(res) {
                            const emp_id = res.message?.employee;
                            if (emp_id) {
                                const row = frm.add_child('employee');
                                row.employee = emp_id;
                            }

                            pending--;

                            if (pending === 0) {
                                frm.refresh_field('employee');
                            }
                        },
                        error: function(err) {
                            console.error('Fetch failed:', err);
                            pending--;
                        }
                    });
                });
            }
        });
    }
});

