async function set_testing_required(frm){
    if(frm.is_new()){
        var inspection_required = ""
        var product_inspection_required = ""
        var application_inspection_required = ""
        if(["Manufacture", "Material Transfer for Manufacture"].includes(frm.doc.stock_entry_type)){
            await frappe.db.get_value('BOM', frm.doc.bom_no, ["inspection_required", "product_inspection_required", "application_inspection_required"])
            .then(r => {
                const value = r.message
                if(frm.doc.stock_entry_type === "Material Transfer for Manufacture"){
                    inspection_required = value.inspection_required
                }
                else if (frm.doc.stock_entry_type === "Manufacture"){
                    product_inspection_required = value.product_inspection_required
                    application_inspection_required = value.application_inspection_required
                }
            })
        }
        frm.set_value("inspection_required", inspection_required)
        frm.set_value("product_inspection_required", product_inspection_required)
        frm.set_value("application_inspection_required", application_inspection_required)
	}
}

function make_quality_inspection(frm, scope) {
	let data = [];
	const fields = [
		{
			label: "Items",
			fieldtype: "Table",
			fieldname: "items",
			cannot_add_rows: true,
			in_place_edit: true,
			data: data,
			get_data: () => {
				return data;
			},
			fields: [
				{
					fieldtype: "Data",
					fieldname: "docname",
					hidden: true
				},
				{
					fieldtype: "Read Only",
					fieldname: "item_code",
					label: __("Item Code"),
					in_list_view: true
				},
				{
					fieldtype: "Read Only",
					fieldname: "item_name",
					label: __("Item Name"),
					in_list_view: true
				},
				{
					fieldtype: "Float",
					fieldname: "qty",
					label: __("Accepted Quantity"),
					in_list_view: true,
					read_only: true
				},
				{
					fieldtype: "Float",
					fieldname: "sample_size",
					label: __("Sample Size"),
					reqd: true,
					in_list_view: true
				},
				{
					fieldtype: "Data",
					fieldname: "description",
					label: __("Description"),
					hidden: true
				},
				{
					fieldtype: "Data",
					fieldname: "serial_no",
					label: __("Serial No"),
					hidden: true
				},
				{
					fieldtype: "Data",
					fieldname: "batch_no",
					label: __("Batch No"),
					hidden: true
				},
				{
					fieldtype: "Data",
					fieldname: "child_row_reference",
					label: __("Child Row Reference"),
					hidden: true
				}
			]
		}
	];

// 	const me = this;
	const dialog = new frappe.ui.Dialog({
		title: __(`Select Items for ${scope} Inspection`),
		size: "extra-large",
		fields: fields,
		primary_action: function () {
			const data = dialog.get_values();
			frappe.call({
				method: "mohan_impex.mohan_impex.stock_entry.make_quality_inspections",
				args: {
					doctype: frm.doc.doctype,
					docname: frm.doc.name,
					items: data.items,
					scope: scope
				},
				freeze: true,
				callback: function (r) {
					if (r.message.length > 0) {
						if (r.message.length === 1) {
							frappe.set_route("Form", "Quality Inspection", r.message[0]);
						} else {
							frappe.route_options = {
								"reference_type": frm.doc.doctype,
								"reference_name": frm.doc.name
							};
							frappe.set_route("List", "Quality Inspection");
						}
					}
					dialog.hide();
				}
			});
		},
		primary_action_label: __("Create")
	});
    console.log(frm.doc.items)
	frappe.call({
		method: "mohan_impex.mohan_impex.stock_entry.check_item_quality_inspection",
		args: {
			doctype: frm.doc.doctype,
			items: frm.doc.items,
			inspection_scope: scope
		},
		freeze: true,
		callback: function (r) {
		    console.log(r.message)
			r.message.forEach(item => {
				if (has_inspection_required(frm, item, scope)) {
					let dialog_items = dialog.fields_dict.items;
					dialog_items.df.data.push({
						"item_code": item.item_code,
						"item_name": item.item_name,
						"qty": item.qty,
						"description": item.description,
						"serial_no": item.serial_no,
						"batch_no": item.batch_no,
						"sample_size": item.sample_quantity,
						"child_row_reference": item.name,
					});
					dialog_items.grid.refresh();
				}
			});

			data = dialog.fields_dict.items.df.data;
			if (!data.length) {
				frappe.msgprint(__("All items in this document already have a linked Quality Inspection."));
			} else {
				dialog.show();
			}
		}
	});
}

function has_inspection_required(frm, item, scope) {
    let inspection_value = item.quality_inspection
    if (scope === "Product"){
        inspection_value = item.product_inspection
    }
    else if (scope === "Application"){
        inspection_value = item.application_inspection
    }
    console.log(scope)
	if (frm.doc.doctype === "Stock Entry" && frm.doc.purpose == "Manufacture" ) {
		if (item.is_finished_item && !item.quality_inspection) {
			return true;
		}
	} else if (!inspection_value) {
		return true;
	}
}

frappe.ui.form.on('Payment Entry', {
    party_type: function(frm) {
        frm.set_value('supplier_type', '');
        frm.set_df_property('custom_import_document', 'hidden', 1);
        frm.clear_table('custom_import_document');
        frm.refresh_field('custom_import_document');
    },

    party: function(frm) {
        if (frm.doc.party_type === "Supplier" && frm.doc.party) {
            frappe.db.get_value('Supplier', frm.doc.party, 'supplier_type')
                .then(r => {
                    if (r && r.message && r.message.supplier_type) {
                        const supplier_type = r.message.supplier_type;
                        frm.set_value('supplier_type', supplier_type);

                        const is_import = supplier_type === 'Import';
                        frm.set_df_property('custom_import_document', 'hidden', !is_import);

                        frm.clear_table('custom_import_document');

                        if (is_import) {
                            const rows = [
                                'Remittance Copy',
                                'Final Payment',
                                'Advance Payment / DA / DP / LC'
                            ];

                            rows.forEach(text => {
                                let row = frm.add_child('custom_import_document');
                                row.documents = text; // <- this is your field
                            });

                            frm.refresh_field('custom_import_document');
                        } else {
                            frm.refresh_field('custom_import_document');
                        }
                    } else {
                        frm.set_value('supplier_type', '');
                        frm.set_df_property('custom_import_document', 'hidden', 1);
                        frm.clear_table('custom_import_document');
                        frm.refresh_field('custom_import_document');
                    }
                });
        } else {
            frm.set_value('supplier_type', '');
            frm.set_df_property('custom_import_document', 'hidden', 1);
            frm.clear_table('custom_import_document');
            frm.refresh_field('custom_import_document');
        }
    }
});

