frappe.ui.form.on('Transport RFQ', {
    onload: function(frm) {
        frappe.call({
            method: "mohan_impex.user_roles.transporter_user",
            callback: function(r) {
                if (r.message) {
                    // Check if the row already exists
                    let existing_rows = frm.doc.transporter_details.filter(row => row.transporter === r.message);

                    if (existing_rows.length === 1 && frm.doc.transporter_details.length === 1) {
                        // Don't touch the form if it's already set up properly
                        return;
                    }

                    // Replace with only the matching row (or add it if missing)
                    frm.clear_table("transporter_details");

                    let row = frm.add_child("transporter_details");

                    if (existing_rows.length) {
                        Object.assign(row, existing_rows[0]); // copy data if already exists
                    } else {
                        row.transporter = r.message; // set if new
                    }

                    frm.refresh_field("transporter_details");
                }
            }
        });
    }
});

frappe.ui.form.on('Transporter Details', {
    send: function(frm, cdt, cdn) {
        let detail_row = locals[cdt][cdn];

        if (!detail_row.transporter || !detail_row.quoted_amount) {
            frappe.msgprint("Transporter or Quoted Amount is missing.");
            return;
        }

        let matched = false;

        // Loop through 'transporters' table in the same doc
        frm.doc.transporters.forEach(row => {
            if (row.transporter === detail_row.transporter) {
                row.quoted_amount = detail_row.quoted_amount;
                matched = true;
            }
        });

        if (matched) {
            frm.refresh_field("transporters");
            frappe.msgprint("Quoted amount updated in Transporters table.");
        } else {
            frappe.msgprint("Matching transporter not found in Transporters table.");
        }
    }
});

frappe.ui.form.on('Transport RFQ', {
    onload: function(frm) {
        // Fetch current user transporter and add to transporter_details (no clearing)
        frappe.call({
            method: "mohan_impex.user_roles.transporter_user", // update path if needed
            callback: function(r) {
                if (r.message) {
                    // Check if transporter already exists in the child table
                    let exists = frm.doc.transporter_details.some(row => row.transporter === r.message);
                    if (!exists) {
                        let row = frm.add_child("transporter_details");
                        row.transporter = r.message;
                        frm.refresh_field("transporter_details");
                    }
                }
            }
        });
    }
});

frappe.ui.form.on('Supplier Quotation', {
    onload: function(frm) {
        let allowedRoles = [
            "Administrator", 
            "Purchase Executive", 
            "Purchase Manager", 
            "Purchase Master Manager", 
            "Purchase User", 
            "SCM Manager"
        ];

        let hasAccess = frappe.user_roles.some(role => allowedRoles.includes(role));

        // Only clear if it's a new doc AND user is unauthorized
        if (!hasAccess && frm.is_new()) {
            frm.doc.items.forEach(item => {
                item.rate = 0;
                item.amount = 0;
            });
            frm.refresh_field('items');
        }
    }
});

frappe.ui.form.on('Supplier Quotation Item', {
    item_code: function (frm, cdt, cdn) {
        const row = frappe.model.get_doc(cdt, cdn);

        // Clear brand if item_code is empty
        if (!row.item_code) {
            frappe.model.set_value(cdt, cdn, 'custom_select_brand', '');
            frm.fields_dict.items.grid.update_docfield_property(
                'custom_select_brand',
                'options',
                ['']
            );
            return;
        }

        frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "Item",
                name: row.item_code
            },
            callback: function (r) {
                if (!r.exc && r.message) {
                    const brand_table = r.message.custom_select_brand || [];
                    const brand_options = brand_table.map(d => d.brand_item);

                    // Update brand select options for the *specific row*
                    frm.fields_dict.items.grid.update_docfield_property(
                        'custom_select_brand',
                        'options',
                        [''].concat(brand_options)
                    );

                    // Clear selected brand value to avoid mismatch
                    frappe.model.set_value(cdt, cdn, 'custom_select_brand', '');

                    // Refresh only this row
                    frm.fields_dict.items.grid.refresh_row(row);
                }
            }
        });
    }
});

