frappe.ui.form.on("Request for Quotation", {
	setup: function (frm) {
		frm.custom_make_buttons = {
			"Supplier Quotation": "Create",
		};

		frm.fields_dict["suppliers"].grid.get_field("contact").get_query = function (doc, cdt, cdn) {
			let d = locals[cdt][cdn];
			return {
				query: "frappe.contacts.doctype.contact.contact.contact_query",
				filters: {
					link_doctype: "Supplier",
					link_name: d.supplier || "",
				},
			};
		};

		frm.set_query("warehouse", "items", () => ({
			filters: {
				company: frm.doc.company,
				is_group: 0,
			},
		}));
	},

	onload: function (frm) {
		if (!frm.doc.message_for_supplier) {
			frm.set_value("message_for_supplier", __("Please supply the specified items at the best possible rates"));
		}
	},

	refresh: function (frm) {
		if (frm.doc.docstatus === 1) {
			frm.add_custom_button(__("Supplier Quotation"), function () {
				frm.trigger("make_supplier_quotation");
			}, __("Create"));
		}
	},

	make_supplier_quotation: function (frm) {
		frappe.call({
			method: "mohan_impex.user_roles.get_supplier_for_user",
			callback: function (r) {
				if (r.message) {
					let supplier = r.message;
					if (!frm.doc.suppliers.map(row => row.supplier).includes(supplier)) {
						frappe.msgprint(__("Your supplier is not part of this RFQ."));
						return;
					}
					frappe.call({
						type: "GET",
						method: "erpnext.buying.doctype.request_for_quotation.request_for_quotation.make_supplier_quotation_from_rfq",
						args: {
							source_name: frm.doc.name,
							for_supplier: supplier,
						},
						freeze: true,
						callback: function (res) {
							if (!res.exc) {
								var doc = frappe.model.sync(res.message);
								frappe.set_route("Form", res.message.doctype, res.message.name);
							}
						}
					});
				} else {
					var doc = frm.doc;
					var dialog = new frappe.ui.Dialog({
						title: __("Create Supplier Quotation"),
						fields: [
							{
								fieldtype: "Link",
								label: __("Supplier"),
								fieldname: "supplier",
								options: "Supplier",
								reqd: 1,
								get_query: () => {
									return {
										filters: [["Supplier", "name", "in", frm.doc.suppliers.map((row) => row.supplier)]],
									};
								},
							},
						],
						primary_action_label: __("Create"),
						primary_action: (args) => {
							if (!args) return;
							dialog.hide();
							return frappe.call({
								type: "GET",
								method: "erpnext.buying.doctype.request_for_quotation.request_for_quotation.make_supplier_quotation_from_rfq",
								args: {
									source_name: doc.name,
									for_supplier: args.supplier,
								},
								freeze: true,
								callback: function (r) {
									if (!r.exc) {
										var doc = frappe.model.sync(r.message);
										frappe.set_route("Form", r.message.doctype, r.message.name);
									}
								}
							});
						},
					});
					dialog.show();
				}
			}
		});
	}
});

frappe.ui.form.on('Request for Quotation', {
    refresh: function(frm) {
        if (!frm.doc.suppliers || frm.doc.suppliers.length === 0) {
            frm.disable_save();
            frappe.throw(__('No suppliers found in this RFQ.'));
            return;
        }

        // Variable to track if logged-in user is a supplier
        let is_supplier = false;

        // Iterate through each supplier in the RFQ
        frm.doc.suppliers.forEach(supplier => {
            if (supplier.email_id === frappe.session.user) {
                is_supplier = true;
            }
        });

        if (is_supplier) {
            // Grant full permissions if user is a supplier
            frm.perm = [{read: 1, write: 1, create: 1, submit: 1, cancel: 1}];
            frm.refresh();
        } else {
            // Restrict access if user is NOT a supplier
            frm.disable_save();
            frappe.throw(__('You do not have permission to access this document.'));
        }
    }
});



