}

let original_transporters = [];

frappe.ui.form.on('Transport RFQ', {
  refresh: function (frm) {
    // Add a custom button to apply filter
    frm.add_custom_button("Filter: Test Supplier", function () {
      // Backup full list only once
      if (original_transporters.length === 0) {
        original_transporters = JSON.parse(JSON.stringify(frm.doc.transporters));
      }

      // Filter matching rows
      const filtered = original_transporters.filter(row => row.transporter_name === "Test Supplier");

      // Replace table content with filtered data
      frm.clear_table('transporters');
      filtered.forEach(row => {
        const new_row = frm.add_child('transporters');
        Object.assign(new_row, row);
      });
      frm.refresh_field('transporters');
    });

    // Add a reset button to restore all original rows
    frm.add_custom_button("Clear Filter", function () {
      if (original_transporters.length > 0) {
        frm.clear_table('transporters');
        original_transporters.forEach(row => {
          const new_row = frm.add_child('transporters');
          Object.assign(new_row, row);
        });
        frm.refresh_field('transporters');
      }
    });
  }
});

