frappe.ui.form.on('Employee Checkin', {
    validate: function(frm) {
        // Set the fixed entry time as 10:00 AM
        const entry_time = moment("10:00 AM", "h:mm A"); // Fixed entry time: 10 AM
        const actual_time = moment(frm.doc.checkin_time); // Actual check-in time entered by employee

        // Calculate the time difference in minutes
        if (actual_time.isValid()) {
            let time_diff = actual_time.diff(entry_time, 'minutes'); // Difference in minutes

            if (Math.abs(time_diff) > 15) { // Only consider entries outside the 15-minute window
                const hours = Math.floor(Math.abs(time_diff) / 60);
                const minutes = Math.abs(time_diff) % 60;

                // Set the custom_entry field based on the time difference
                if (time_diff < 0) {
                    frm.set_value('entry', 'Early Entry');
                } else {
                    frm.set_value('entry', 'Late Entry');
                }

                // Set the custom_time_diff field in hours and minutes
                frm.set_value('time_diff', `${hours} hour${hours > 1 ? 's' : ''} ${minutes} minute${minutes > 1 ? 's' : ''}`);
            } else {
                // If within the 15-minute window, mark as "On Time"
                frm.set_value('entry', 'On Time');
                frm.set_value('time_diff', '0 hours 0 minutes');
            }

        } else {
            frappe.msgprint(__('Invalid Check-in Time'));
        }
    }
});

