frappe.ui.form.on('Material Request Item', {
    item_code: function(frm, cdt, cdn) {
        var row = locals[cdt][cdn];

        // Fetching Item details including Item Name, Pack Type, and Pack Size
        if (row.item_code) {
            frappe.call({
                method: 'frappe.client.get',
                args: {
                    doctype: 'Item',
                    name: row.item_code
                },
                callback: function(response) {
                    if (response.message) {
                        let itemData = response.message;

                        // Set Item Name
                        frappe.model.set_value(cdt, cdn, 'item_name', itemData.item_name);

                        // Ensure that the 'brand' field is a Select field in the child table
                        let brandField = frm.fields_dict['items'].grid.get_field('brand');
                        let brandOptions = [];

                        // Add options from select_brand array to the brand field
                        if (Array.isArray(itemData.select_brand) && itemData.select_brand.length > 0) {
                            itemData.select_brand.forEach(function(brand) {
                                brandOptions.push(brand.brand_item); // Adjust based on the correct structure
                            });
                        }

                        // If there's only one brand_name, set it as the default value
                        if (itemData.brand_name && itemData.select_brand.length === 0) {
                            brandOptions.push(itemData.brand_name);
                            frappe.model.set_value(cdt, cdn, 'brand', itemData.brand_name);
                        }

                        // If no brand data is found, set "Brand Not Available"
                        if (brandOptions.length === 0) {
                            // Add "Brand Not Available" as a selectable option in the Select field
                            brandOptions.push('Brand Not Available');
                            frappe.model.set_value(cdt, cdn, 'brand', 'Brand Not Available');
                        }

                        // Set the options for the Select field dynamically
                        brandField.options = brandOptions.join("\n");

                        // Refresh the field to reflect changes in the form
                        frm.fields_dict['items'].grid.refresh();

                    } else {
                        frappe.msgprint(__('Item details not found for the given Item Code'));
                    }
                },
                error: function(error) {
                    console.error('Error fetching Item details:', error);
                    frappe.msgprint(__('Unable to fetch Item details. Please try again.'));
                }
            });
        }
    }
});

