frappe.ui.form.on('Employee', {
    refresh: function(frm) {
        // Add a custom button to the Employee form
        frm.add_custom_button(__('View Attendance'), function() {
            // Call the method to fetch employee attendance data
            frappe.call({
                method: 'erpnext.attendance.employee_attendance', // Adjust to your actual method path
                callback: function(response) {
                    // Check if data was returned
                    if (response.message && response.message.data) {
                        // Create a simple HTML table to display the attendance data
                        let table_html = `
                            <table class="table table-bordered">
                                <thead>
                                    <tr>
                                        <th>Employee ID</th>
                                        <th>Employee Name</th>
                                        <th>Location</th>
                                        <th>Shift</th>
                                        <th>In Time</th>
                                        <th>Out Time</th>
                                        <th>Status</th>
                                    </tr>
                                </thead>
                                <tbody>
                        `;

                        // Loop through the data and create table rows
                        response.message.data.forEach(function(row) {
                            table_html += `
                                <tr>
                                    <td>${row.EMPLOYEE_ID}</td>
                                    <td>${row.EMPLOYEE_NAME}</td>
                                    <td>${row.LOCATION}</td>
                                    <td>${row.SHIFT}</td>
                                    <td>${row.IN_TIME}</td>
                                    <td>${row.OUT_TIME}</td>
                                    <td>${row.STATUS}</td>
                                </tr>
                            `;
                        });

                        table_html += `</tbody></table>`;

                        // Display the table in a dialog
                        var dialog = new frappe.ui.Dialog({
                            title: 'Employee Attendance Data',
                            fields: [
                                {
                                    'fieldtype': 'HTML',
                                    'fieldname': 'attendance_table',
                                    'options': table_html
                                }
                            ]
                        });

                        dialog.show();
                    } else {
                        // If no data returned, show a message
                        frappe.msgprint(__('No attendance data found.'));
                    }
                }
            });
        });
    }
});

