// Function to fetch attributes dynamically from the selected Item Template
function fetch_variant_attributes(template, frm, cdt, cdn) {
    frappe.call({
        method: "frappe.client.get",
        args: {
            doctype: "Item",
            name: template
        },
        callback: function(response) {
            if (response.message) {
                let item_template = response.message;
                let attributes = item_template.attributes || [];

                if (attributes.length > 0) {
                    let attribute_list = attributes.map(attr => attr.attribute);
                    iterate_attributes_selection(template, attribute_list, {}, frm, cdt, cdn);
                } else {
                    frappe.msgprint("No variant attributes found for this template.");
                }
            }
        }
    });
}

// Function to iterate through each attribute selection
function iterate_attributes_selection(template, attributes, selected_values, frm, cdt, cdn) {
    if (attributes.length === 0) {
        fetch_matching_variant(template, selected_values, frm, cdt, cdn);
        return;
    }

    let current_attr = attributes.shift();

    frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Item Attribute Value",
            filters: { "parent": current_attr },
            fields: ["attribute_value"]
        },
        callback: function(response) {
            if (response.message.length > 0) {
                let options = response.message.map(attr => ({
                    label: attr.attribute_value,
                    value: attr.attribute_value
                }));

                frappe.prompt([
                    {
                        label: `Select ${current_attr}`,
                        fieldname: current_attr,
                        fieldtype: "Select",
                        options: options
                    }
                ], (data) => {
                    selected_values[current_attr] = data[current_attr];
                    iterate_attributes_selection(template, attributes, selected_values, frm, cdt, cdn);
                });
            } else {
                frappe.msgprint(`No values found for attribute ${current_attr}`);
            }
        }
    });
}

// Function to fetch final variant based on selected attributes
function fetch_matching_variant(template, selected_values, frm, cdt, cdn) {
    let filters = { "variant_of": template };

    // Add selected attribute values as filters
    Object.keys(selected_values).forEach(attr => {
        filters[`attribute_${attr.toLowerCase()}`] = selected_values[attr];
    });

    frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Item",
            filters: filters,
            fields: ["item_code", "item_name"]
        },
        callback: function(response) {
            if (response.message.length === 1) {
                // Auto-select the final variant
                frappe.model.set_value(cdt, cdn, "item_code", response.message[0].item_code);
            } else if (response.message.length > 1) {
                // If multiple variants match, allow user to choose
                let options = response.message.map(item => ({
                    label: item.item_name,
                    value: item.item_code
                }));

                frappe.prompt([
                    {
                        label: "Select Final Variant",
                        fieldname: "final_variant",
                        fieldtype: "Select",
                        options: options
                    }
                ], (final_data) => {
                    frappe.model.set_value(cdt, cdn, "item_code", final_data.final_variant);
                });
            } else {
                frappe.msgprint("No matching variant found. Please check attribute selections.");
            }
        }
    });
}

