function update_labour_cost(frm) {
    if (frm.doc.purchase_receipts && frm.doc.purchase_receipts.length > 0) {
        let receipt_document_type = frm.doc.purchase_receipts[0].receipt_document_type;
        let receipt_document = frm.doc.purchase_receipts[0].receipt_document;

        // Ensure it is a Purchase Receipt
        if (receipt_document_type === 'Purchase Receipt' && receipt_document) {
            frappe.call({
                method: "frappe.client.get_value",
                args: {
                    doctype: "Purchase Receipt",
                    filters: { name: receipt_document },
                    fieldname: "custom_total_labour_cost"
                },
                callback: function(response) {
                    if (response.message) {
                        let total_labour_cost = response.message.custom_total_labour_cost || 0;

                        // Ensure at least one row exists in the Taxes and Charges table
                        if (!frm.doc.taxes || frm.doc.taxes.length === 0) {
                            let row = frm.add_child("taxes");
                            row.description = "Labour Cost";
                            row.amount = total_labour_cost;
                        } else {
                            frm.doc.taxes[0].description = "Labour Cost";
                            frm.doc.taxes[0].amount = total_labour_cost;
                        }

                        frm.refresh_field("taxes");
                        frappe.msgprint(__('Labour Cost updated from Purchase Receipt: ' + receipt_document));
                    }
                }
            });
        }
    }
}


