frappe.ui.form.on('Request for Quotation', {
    onload_post_render(frm) {
        if (frm.doc.__islocal) return;

        frm.dashboard.add_transactions = function () {
            frm.dashboard.add_transaction({
                items: [
                    {
                        label: 'Supplier Quotation',
                        doctype: 'Supplier Quotation',
                        on_click: () => {
                            const doc = frm.doc;

                            // Fetch supplier linked to current user
                            frappe.call({
                                method: "mohan_impex.user_roles.get_supplier_for_user",
                                args: { user: frappe.session.user },
                                callback(res) {
                                    const linked_supplier = res.message;

                                    if (!linked_supplier) {
                                        frappe.msgprint(__('You are not linked to any Supplier.'));
                                        return;
                                    }

                                    const suppliers = (doc.suppliers || []).map(row => row.supplier);

                                    if (!suppliers.includes(linked_supplier)) {
                                        frappe.msgprint(__('You are not one of the suppliers invited for this RFQ.'));
                                        return;
                                    }

                                    // Create dialog
                                    const dialog = new frappe.ui.Dialog({
                                        title: __("Create Supplier Quotation"),
                                        fields: [
                                            {
                                                fieldtype: "Link",
                                                label: __("Supplier"),
                                                fieldname: "supplier",
                                                options: "Supplier",
                                                reqd: 1
                                            }
                                        ],
                                        primary_action_label: __("Create"),
                                        primary_action(values) {
                                            frappe.call({
                                                method: "erpnext.buying.doctype.request_for_quotation.request_for_quotation.make_supplier_quotation",
                                                args: {
                                                    source_name: doc.name,
                                                    supplier: values.supplier
                                                },
                                                callback(res) {
                                                    if (!res.exc && res.message) {
                                                        frappe.set_route("Form", res.message.doctype, res.message.name);
                                                    }
                                                }
                                            });
                                            dialog.hide();
                                        }
                                    });

                                    dialog.show();

                                    // Set the supplier value after showing dialog
                                    dialog.set_value("supplier", linked_supplier);

                                    // Optional: Make it read-only
                                    dialog.fields_dict.supplier.df.read_only = 1;
                                    dialog.fields_dict.supplier.refresh();
                                }
                            });
                        }
                    }
                ]
            });
        };
    }
});

frappe.ui.form.on("Purchase Order Item", {
    item_code: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        let supplier = frm.doc.supplier;

        if (supplier && row.item_code) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Item",
                    name: row.item_code
                },
                callback: function(r) {
                    if (r.message && r.message.supplier_items) {
                        let matched = r.message.supplier_items.find(s => s.supplier === supplier);

                        if (matched) {
                            if (matched.custom_lead_time_in_days && frm.doc.transaction_date) {
                                let expected_date = frappe.datetime.add_days(
                                    frm.doc.transaction_date,
                                    matched.custom_lead_time_in_days
