frappe.ui.form.on('Purchase Receipt', {
    refresh: function(frm) {
        //  Add 'Create GRN 2' button if submitted
        if (frm.doc.docstatus === 1) {
            frm.add_custom_button(__('Create GRN 2'), function() {
                frappe.model.with_doctype('GRN 2', function() {
                    let new_doc = frappe.model.get_new_doc('GRN 2');
                    new_doc.purchase_receipt = frm.doc.name;
                    new_doc.type = frm.doc.custom_type;
                    new_doc.invoice_no = frm.doc.custom_invoice_no;
                    new_doc.invoice_date = frm.doc.custom_invoice_date;

                    frappe.set_route('Form', 'GRN 2', new_doc.name);
                });
            });
        }

        //  Safely set custom_type from linked PO
        if (frm.doc.name) {
            frappe.call({
                method: "mohan_impex.PR_Connection.get_linked_purchase_order",
                args: { purchase_receipt: frm.doc.name },
                callback: function(response) {
                    if (response.message && typeof response.message === "string") {
                        let purchase_order = response.message;
                        let custom_type = '';

                        if (purchase_order.startsWith("IMP")) {
                            custom_type = 'Import';
                        } else if (purchase_order.startsWith("DOM")) {
                            custom_type = 'Domestic';
                        }

                        if (!frm.doc.custom_type || frm.doc.custom_type !== custom_type) {
                            frm.set_value('custom_type', custom_type);
                        }
                    }
                }
            });
        }

        //  Sync custom item details
        sync_custom_item_details(frm);
        set_item_code_filter(frm);
    },

    on_submit: function(frm) {
        //  Pull labour cost from linked PO
        if (frm.doc.items && frm.doc.items.length > 0) {
            let purchase_order = frm.doc.items[0].purchase_order;

            if (purchase_order) {
                frappe.call({
                    method: "frappe.client.get_value",
                    args: {
                        doctype: "Purchase Order",
                        filters: { name: purchase_order },
                        fieldname: "custom_total_labour_cost"
                    },
                    callback: function(response) {
                        if (response.message) {
                            let total_labour_cost = response.message.custom_total_labour_cost || 0;

                            frappe.call({
                                method: "frappe.client.set_value",
                                args: {
                                    doctype: "Purchase Receipt",
                                    name: frm.doc.name,
                                    fieldname: {
                                        "custom_total_labour_cost": total_labour_cost
                                    }
                                },
                                callback: function(update_response) {
                                    if (!update_response.exc) {
                                        frappe.msgprint(__('Total Labour Cost updated from Purchase Order: ' + total_labour_cost));
                                    }
                                }
                            });
                        }
                    }
                });
            } else {
                frappe.msgprint(__('No linked Purchase Order found for this Purchase Receipt.'));
            }
        }
    },

    //  Trigger custom_item_details sync on add/remove rows
    items_add: function(frm, cdt, cdn) {
        sync_custom_item_details(frm);
    },
    items_remove: function(frm, cdt, cdn) {
        sync_custom_item_details(frm);
    }
});

