frappe.ui.form.on('Employee', {
	refresh(frm) {
		// Ensure that we set the value if the 'date_of_joining' is already set when the form lo
	},
	before_save(frm){
	    if (frm.doc.date_of_joining) {
			update_years_months_days(frm);
		}
	},
	// This will trigger whenever 'date_of_joining' field is changed
	date_of_joining: function (frm) {
		// When the date_of_joining is changed, recalculate years, months, and days
		if (frm.doc.date_of_joining) {
			update_years_months_days(frm);
		}
	}
});
function update_years_months_days(frm) {
	const joining_date = frm.doc.date_of_joining;
	const current_date = frappe.datetime.nowdate();
	// Calculate the difference in days
	const total_days_diff = frappe.datetime.get_diff(current_date, joining_date);
	// Calculate the number of full years
	const years = Math.floor(total_days_diff / 365);
	// Calculate the remaining days after extracting years
	const remaining_days_after_years = total_days_diff - years * 365;
	// Calculate the number of full months
	const months = Math.floor(remaining_days_after_years / 30); // Approximate months (30 days p
	// Calculate the remaining days after extracting months
	const remaining_days = remaining_days_after_years - months * 30; // Approximate remaining da
	// Format the result as "X years Y months Z days"
	let years_and_months_days = `${years} years`;
	if (months > 0) {
		years_and_months_days += ` ${months} months`;
	}
	if (remaining_days > 0) {
		years_and_months_days += ` ${remaining_days} days`;
	}
	// Set the value in the custom field
	frm.set_value('custom__total_number_of_year_in_mi', years_and_months_days);
}

