// ----------- Utilities ------------

function load_custom_documents(frm) {
    let docs = [];

    if (frm.doc.custom_type === "Domestic") {
        docs = ["Invoice Copy", "COA Copy", "E-way Bill", "External Test Report", "Additional Documents"];
    } else if (frm.doc.custom_type === "Import") {
        docs = ["Invoice Copy", "COA Copy", "E-way Bill", "Halal Certificate (For Export)", "Kosher Certificate (For Export)", "External Test Report", "Additional Documents"];
    }

    frm.clear_table('custom_documents');

    docs.forEach(name => {
        let row = frm.add_child('custom_documents');
        row.document_name = name;
    });

    frm.refresh_field('custom_documents');
    console.log(" Loaded custom_documents:", cur_frm.doc.custom_documents);
}

function load_custom_item_details(frm) {
    if (!frm.doc.items || frm.doc.items.length === 0) return;

    frm.clear_table('custom_item_details');

    frm.doc.items.forEach(item => {
        if (!item.item_code) return;

        let expected = flt(item.qty) + flt(item.rejected_qty || 0);
        let received = flt(item.qty);

        let row = frm.add_child('custom_item_details');
        row.item_code = item.item_code;
        row.expected_qty = expected;
        row.received_qty = received;
        row.unit_price = item.rate;
        row.batch = item.batch_no;
        row.condition = (expected === received) ? 'All okay' : 'Short';
    });

    frm.refresh_field('custom_item_details');
    console.log(" Loaded custom_item_details:", cur_frm.doc.custom_item_details);
}

function sync_custom_item_details(frm) {
    if (!frm.doc.items || frm.doc.items.length === 0) return;

    frm.clear_table('custom_item_details');

    frm.doc.items.forEach(item => {
        if (!item.item_code) return;

        let expected = flt(item.qty) + flt(item.rejected_qty || 0);
        let received = flt(item.qty);

        let row = frm.add_child('custom_item_details');
        row.item_code = item.item_code;
        row.expected_qty = expected;
        row.received_qty = received;
        row.unit_price = item.rate;
        row.batch = item.batch_no;
        row.condition = (expected === received) ? 'All okay' : 'Short';
    });

    frm.refresh_field('custom_item_details');
}

