                    frm.set_value('custom_driver_name', r.message.custom_driver_name || '');
                    frm.set_value('custom_driver_mobile_number', r.message.custom_driver_mobile_number || '');
                }
            });
        }
    },
    custom_type: function(frm) {
        // Run only for NEW documents and only if table is empty daiyan
        if (frm.is_new() && (!frm.doc.custom_documents || frm.doc.custom_documents.length === 0)) {
            let default_documents = [];

            if (frm.doc.custom_type === "Domestic") {
                default_documents = ["Invoice Copy", "COA Copy", "E-way Bill", "External Test Report", "Additional Documents"];
            } else if (frm.doc.custom_type === "Import") {
                default_documents = ["Invoice Copy", "COA Copy", "E-way Bill", "Halal Certificate (For Export)", "Kosher Certificate (For Export)", "External Test Report", "Additional Documents"];
            }

            // Clear the table first
            frm.clear_table('custom_documents');

            // Add new rows
            default_documents.forEach(doc => {
                let row = frm.add_child('custom_documents');
                row.document_name = doc;
            });

            frm.refresh_field('custom_documents');
            console.log(" Loaded custom_documents:", cur_frm.doc.custom_documents);
        }
    },
    after_save: function(frm) {
    if (frm.doc.custom_asn_no) {
        frappe.call({
            method: "frappe.client.set_value",
            args: {
                doctype: "Advance Supplier Notification",
                name: frm.doc.custom_asn_no,  // this is the ASN docname
                fieldname: {
                    purchase_receipt: frm.doc.name
                }
            },
        });
    }
}

});

