frappe.ui.form.on('Supplier Quotation', {
    before_save(frm) {
        if (frm.doc.workflow_state === "Rejected" && !frm.doc.rejection_remarks) {
            frappe.msgprint({
                title: __("Missing Reject Remarks"),
                message: __("Please write Reject Remarks."),
                indicator: "red"
            });
            frappe.validated = false;
        }
    },

    before_workflow_action(frm) {
        if (frm.selected_workflow_action === "Reject" && !frm.doc.rejection_remarks) {
            frappe.throw(__("Please write Reject Remarks before rejecting this quotation."));
        }
    },
    refresh(frm) {
        let allowedRoles = [
            "Administrator", 
            "Purchase Executive", 
            "Purchase Manager", 
            "Purchase Master Manager", 
            "Purchase User", 
            "SCM Manager"
        ];

        let hasAccess = frappe.user_roles.some(role => allowedRoles.includes(role));

        if (!hasAccess) {
            setTimeout(() => {
                frm.remove_custom_button(__('Purchase Order'), __('Create'));
                frm.remove_custom_button(__('Quotation'), __('Create'));
                frm.remove_custom_button(__('Material Request'), __('Get Items From'));
                frm.remove_custom_button(__('Request for Quotation'), __('Get Items From'));
                frm.remove_custom_button(__('Link to Material Requests'), __('Tools'));
                $(".menu-btn-group").hide();
            }, 500);
        }

        set_item_code_filter(frm);
        apply_rejection_rule(frm);
    }
});

function set_item_code_filter(frm) {
    frm.fields_dict["items"].grid.get_field("item_code").get_query = function(doc, cdt, cdn) {
        let row = locals[cdt][cdn];
        return {
            filters: [
                ["has_variants", "=", 0],
                ["is_sales_item", "=", 1],
                ["variant_of", "=", row.item_template]
            ]
        };
    };
}

function apply_rejection_rule(frm) {
    if (frm.doc.workflow_state === "Rejected") {
        frm.set_df_property("rejection_remarks", "reqd", 1);
    } else {
        frm.set_df_property("rejection_remarks", "reqd", 0);
    }
}

frappe.ui.form.on('Landed Cost Voucher', {
    purchase_receipts_add: function(frm, cdt, cdn) {
        update_labour_cost(frm);
    },
    purchase_receipts_remove: function(frm, cdt, cdn) {
        update_labour_cost(frm);
    },
    refresh: function(frm) {
        update_labour_cost(frm);
    }
});

function update_labour_cost(frm) {
    if (frm.doc.purchase_receipts && frm.doc.purchase_receipts.length > 0) {
        let receipt_document_type = frm.doc.purchase_receipts[0].receipt_document_type;
        let receipt_document = frm.doc.purchase_receipts[0].receipt_document;

        // Ensure it is a Purchase Receipt
        if (receipt_document_type === 'Purchase Receipt' && receipt_document) {
            frappe.call({
                method: "frappe.client.get_value",
                args: {
                    doctype: "Purchase Receipt",
                    filters: { name: receipt_document },
                    fieldname: [
                        "custom_total_labour_cost",      // labour cost
                        "total_misc_amount",      // misc amount (assumed fieldname)
                        "transport_charges"              // transport charges (assumed fieldname)
                    ]
                },
                callback: function(response) {
                    if (response.message) {
                        let pr_vals = response.message;

                        let total_labour_cost  = pr_vals.custom_total_labour_cost  || 0;
                        let total_misc_amount  = pr_vals.total_misc_amount  || 0;
                        let transport_charges  = pr_vals.transport_charges         || 0;

                        // helper to create / update a row in taxes table
                        const ensure_tax_row = (description, amount) => {
                            if (!amount || amount == 0) return; // skip zero values

                            // make sure taxes exists
                            if (!frm.doc.taxes) {
                                frm.doc.taxes = [];
                            }

                            // try to find existing row by description
                            let row = frm.doc.taxes.find(d => d.description === description);

                            // if not found, add a new row
                            if (!row) {
                                row = frm.add_child("taxes");
                            }

                            row.description = description;
                            row.amount = amount;
                        };

                        // set / update three rows
                        ensure_tax_row("Labour Cost", total_labour_cost);
                        ensure_tax_row("Misc Amount", total_misc_amount);
                        ensure_tax_row("Transport Charges", transport_charges);

                        frm.refresh_field("taxes");
                    }
                }
            });
        }
    }
}

frappe.ui.form.on("Landed Cost Voucher", "validate", function(frm) {
    const today = frappe.datetime.get_today();

    if (frm.doc.posting_date < today) {
        frappe.msgprint(__("Posting Date cannot be in the past."));
        frappe.validated = false;
    }
});

frappe.ui.form.on("Payment Doc Type", {
	links_add(frm, cdt, cdn) {
		// always sync document_type from parent
		frappe.model.set_value(
			cdt,
			cdn,
			"document_type",
			frm.doc.payment_against || ""
		);

		const party_name = (frm.doc.supplier || "").trim();
		const payment_type = (frm.doc.service_payment_type || "").trim();

		const start_date = frm.doc.start_date || null;
		const end_date = frm.doc.end_date || null;

		if (!party_name || !payment_type) {
			frappe.msgprint("Please select Supplier and Service Payment Type first.");
			return;
		}

		if ((start_date && !end_date) || (!start_date && end_date)) {
			frappe.msgprint("Please select both Start Date and End Date.");
			return;
		}

		let method = null;
		if (frm.doc.payment_against === "Purchase Receipt") {
			method = "mohan_impex.pr_invoice.pr_invoice";
		} else if (frm.doc.payment_against === "Delivery Note") {
			method = "mohan_impex.dn_invoice.dn_invoice";
		} else {
			return;
		}

		frappe.call({
			method,
			args: {
				party_name,
				payment_type,
				from_date: start_date,
				to_date: end_date,
			},
			callback: function (r) {
				const data = r.message || [];

				if (!Array.isArray(data) || data.length === 0) {
					frappe.msgprint(
						`No records found for ${frm.doc.payment_against} / ${party_name} / ${payment_type}.`
					);
					return;
				}

				data.forEach((d, i) => {
					let target_row = (i === 0)
						? locals[cdt][cdn]
						: frm.add_child("links");

					target_row.document_type = frm.doc.payment_against;
					target_row.link_name = d.name;          // DN/PR name

					// numeric fields: let Frappe handle type coercion
					target_row.amount = d.amount ?? 0;
					target_row.rate = d.rate ?? 0;
					target_row.total_qty = d.total_qty ?? 0;

					// other fields
					target_row.date = d.date || null;       // posting_date
					target_row.party_name = d.party_name || "";
				});

				frm.refresh_field("links");
			}
		});
	}
});

frappe.listview_settings['Job Card'] = {
    onload(listview) {
        listview.page.add_inner_button(__('Select Employees'), () => {
            // Step 1: Get selected Job Cards
            let selected_jobs = listview.get_checked_items(); // Array of selected Job Card docs
            if (!selected_jobs.length) {
                frappe.msgprint(__('Please select at least one Job Card'));
                return;
            }

            // Step 2: Open Employee Selection Dialog
            let d = new frappe.ui.Dialog({
                title: __('Assign Employees'),
                size: 'large',
                fields: [
                    {
                        fieldname: 'employees',
                        fieldtype: 'Table',
                        label: __('Employee List'),
                        cannot_add_rows: true,
                        cannot_delete_rows: true,
                        in_place_edit: true,
                        fields: [
                            {
                                fieldtype: 'Link',
                                fieldname: 'employee',
                                label: __('Employee'),
                                options: 'Employee',
                                in_list_view: 1,
                                read_only: 1
                            },
                            {
                                fieldtype: 'Data',
                                fieldname: 'employee_name',
                                label: __('Employee Name'),
                                in_list_view: 1,
                                read_only: 1
                            },
                            {
                                fieldtype: 'Link',
                                fieldname: 'department',
                                label: __('Department'),
                                options: 'Department',
                                in_list_view: 1,
                                read_only: 1
                            }
                        ]
                    }
                ],
                primary_action_label: __('Assign To'),
                    primary_action() {
                        let grid = d.get_field('employees').grid;

                        // STAR / LEFT CHECKBOX SE SELECTED ROWS
                        let selected_employees = grid.get_selected_children()
                            .map(row => ({
                                employee: row.employee
                            }));

                        if (!selected_employees.length) {
                            frappe.msgprint(__('Please select at least one employee'));
                            return;
                        }

                        frappe.call({
                            method: 'mohan_impex.job_card.assign_employees',
                            args: {
                                job_cards: selected_jobs.map(j => j.name),
                                employees: JSON.stringify(selected_employees)
                            },
                            callback() {
                                frappe.msgprint(__('Employees assigned successfully'));
                                listview.refresh();
                            }
                        });

                        d.hide();
                    }

            });

            d.show();

            // Step 4: Fetch Active Employees and populate the table
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'Employee',
                    fields: ['name', 'employee_name', 'department'],
                    filters: { status: 'Active' },
                    limit_page_length: 0
                },
                callback(r) {
                    if (!r.message) return;

                    let grid = d.get_field('employees').grid;
                    grid.df.data = r.message.map(emp => ({
                        select: 0,
                        employee: emp.name,
                        employee_name: emp.employee_name,
                        department: emp.department
                    }));
                    grid.refresh();
                }
            });
        });
    }
};

