frappe.ui.form.on('Salary Slip', {
    refresh: function(frm) {
        update_salary_fields(frm);
    },

    leave_without_pay: function(frm) {
        update_salary_fields(frm);
    }
});

function update_salary_fields(frm) {
    const base_day = 30;
    const lop = frm.doc.leave_without_pay || 0;
    const payment_days = base_day - lop;

    frm.set_value('total_working_days', base_day);
    frm.set_value('payment_days', payment_days);

    let total_earnings = 0;

    frm.doc.earnings.forEach(row => {
        row.amount = (row.amount / base_day) * payment_days;
        total_earnings += row.amount;
    });

    frm.set_value('gross_pay', total_earnings);

    const total_deduction = frm.doc.total_deduction || 0;
    const net_pay = total_earnings - total_deduction;
    const rounded_total = Math.round(net_pay);

    frm.set_value('net_pay', net_pay);
    frm.set_value('rounded_total', rounded_total);

    frappe.call({
        method: "mohan_impex.amount_in_word.get_money_in_words",
        args: {
            amount: rounded_total
        },
        callback: function(r) {
            if (r.message) {
                frm.set_value("total_in_words", r.message);
            }
        }
    });

    frm.refresh_field('earnings');
}

let original_transporters = [];

frappe.ui.form.on('Transport RFQ', {
  refresh: function (frm) {
    // Add a custom button to apply filter
    frm.add_custom_button("Filter: Test Supplier", function () {
      // Backup full list only once
      if (original_transporters.length === 0) {
        original_transporters = JSON.parse(JSON.stringify(frm.doc.transporters));
      }

      // Filter matching rows
      const filtered = original_transporters.filter(row => row.transporter_name === "Test Supplier");

      // Replace table content with filtered data
      frm.clear_table('transporters');
      filtered.forEach(row => {
        const new_row = frm.add_child('transporters');
        Object.assign(new_row, row);
      });
      frm.refresh_field('transporters');
    });

    // Add a reset button to restore all original rows
    frm.add_custom_button("Clear Filter", function () {
      if (original_transporters.length > 0) {
        frm.clear_table('transporters');
        original_transporters.forEach(row => {
          const new_row = frm.add_child('transporters');
          Object.assign(new_row, row);
        });
        frm.refresh_field('transporters');
      }
    });
  }
});

