frappe.ui.form.on('Work From Home', 'validate', function(frm) {
    const today = frappe.datetime.get_today(); // Get today's date in "YYYY-MM-DD" format

    // Check if From Date is in the past
    if (frm.doc.from_date && frappe.datetime.str_to_obj(frm.doc.from_date) < frappe.datetime.str_to_obj(today)) {
        frappe.msgprint(__('You cannot select a past date in From Date.'));
        frappe.validated = false; // Prevent form submission
        return;
    }

    // Check if To Date is in the past
    if (frm.doc.to_date && frappe.datetime.str_to_obj(frm.doc.to_date) < frappe.datetime.str_to_obj(today)) {
        frappe.msgprint(__('You cannot select a past date in To Date.'));
        frappe.validated = false; // Prevent form submission
        return;
    }

    // Check if To Date is before From Date
    if (
        frm.doc.from_date && 
        frm.doc.to_date && 
        frappe.datetime.str_to_obj(frm.doc.from_date) > frappe.datetime.str_to_obj(frm.doc.to_date)
    ) {
        frappe.msgprint(__('To Date cannot be before From Date.'));
        frappe.validated = false; // Prevent form submission
        return; // Ensure no further processing happens
    }
});

