frappe.ui.form.on('BOM', {
    onload: function(frm) {
        let parent_groups = ["MP", "PM", "RM"];  // Main groups
        let all_groups = [...parent_groups];  // Store all descendant groups

        function fetchChildGroups(groups, callback) {
            frappe.call({
                method: "frappe.client.get_list",
                args: {
                    doctype: "Item Group",
                    filters: [
                        ["parent_item_group", "in", groups]
                    ],
                    fields: ["name"]
                },
                callback: function(response) {
                    if (response.message.length > 0) {
                        let new_groups = response.message.map(group => group.name);

                        // Add to master list
                        all_groups.push(...new_groups);

                        // Recursively fetch deeper levels
                        fetchChildGroups(new_groups, callback);
                    } else {
                        callback();  // Call callback once recursion is complete
                    }
                }
            });
        }

        // Start recursive fetching
        fetchChildGroups(parent_groups, function() {
            frm.set_query("item", function() {
                return {
                    filters: [
                        ["Item", "item_group", "in", all_groups]
                    ]
                };
            });

            console.log("Final Item Groups Applied:", all_groups);
        });
    }
});


















