frappe.ui.form.on('Supplier Quotation', {
    refresh: function(frm) {
        // Allow hiding only if the user is a "Supplier" and NOT a System Manager or similar privileged roles
        if (frappe.user_roles.includes("Supplier") && 
            !frappe.user_roles.includes("System Manager") && 
            !frappe.user_roles.includes("Purchase User") &&
            !frappe.user_roles.includes("Accounts Manager")) {

            // Hide the "Taxes and Charges" tab
            frm.toggle_display('taxes', false);

            // Hide "Address & Contact", "Terms", and "Connections" tabs
            frm.toggle_display('address_and_contact_section', false);
            frm.toggle_display('terms_section', false);
            frm.toggle_display('connections_section', false);
        }
    }
});

frappe.ui.form.on('Supplier Quotation', {
    refresh: function(frm) {
        // Allow hiding only if the user is a "Supplier" and NOT a System Manager or similar privileged roles
        if (frappe.user_roles.includes("Supplier") && 
            !frappe.user_roles.includes("System Manager") && 
            !frappe.user_roles.includes("Purchase User") &&
            !frappe.user_roles.includes("Accounts Manager")) {

            // Fields to Keep Visible
            let visible_fields = ["item_code", "qty", "uom", "rate", "amount"];

            // Loop through all fields in Items table and hide the ones not in visible_fields
            $.each(frm.fields_dict["items"].grid.fields_map, function (fieldname, field) {
                if (visible_fields.includes(fieldname)) {
                    frm.set_df_property(fieldname, 'hidden', false);  // Show field
                } else {
                    frm.set_df_property(fieldname, 'hidden', true);   // Hide field
                }
            });
        }
    }
});

frappe.ui.form.on('Purchase Order', {
    refresh(frm) {
        // Avoid duplicate buttons on refresh
        frm.remove_custom_button('Create ASN');

        if (frm.doc.custom_acceptance_status === "Accept") {
            frm.add_custom_button('Create ASN', () => {
                if (!Array.isArray(frm.doc.items) || frm.doc.items.length === 0) {
                    frappe.msgprint(__('No items found in this Purchase Order.'));
                    return;
                }

                // Build ASN items from PO items
                const asn_items = frm.doc.items.map(row => ({
                    doctype: "ASN Item",
                    // NOTE: if your ASN child uses item_code, change "item" → "item_code"
                    item: row.item_code,
                    qty: flt(row.qty)
                }));

                // Compute parent total_qty
                const total_qty = asn_items.reduce((a, r) => a + flt(r.qty), 0);

                frappe.call({
                    method: "frappe.client.insert",
                    args: {
                        doc: {
                            doctype: "Advance Supplier Notification",
                            purchase_order: frm.doc.name,
                            supplier: frm.doc.supplier,
                            company: frm.doc.company,
                            items: asn_items,
                            total_qty: total_qty,
                            total_po_qty:total_qty
                        }
                    },
                    freeze: true,
                    freeze_message: __("Creating ASN..."),
                    callback(r) {
                        if (r.message) {
                            frappe.msgprint(
                                __('ASN {0} created. Total Qty: {1}', [r.message.name, total_qty])
                            );
                            frappe.set_route("Form", "Advance Supplier Notification", r.message.name);
                        }
                    }
                });
            });
        }
    }
});

