// Helper to recalc total_qty from child rows
function update_total_qty(frm) {
    let total = 0;
    (frm.doc.items || []).forEach(row => total += flt(row.qty));
    frm.set_value("total_qty", total);
    frm.refresh_field("total_qty");
}

