function calculate_labour_cost(frm) {
    let rate = flt(frm.doc.labour_rate_per_ton || 0); 
    let qty = flt(frm.doc.total_quantity || 0); 
    let total = (rate * qty)/1000;
    frm.set_value('custom_total_labour_cost', total);
}

frappe.ui.form.on('Batch', {
    item: function(frm) {
        if (!frm.doc.item) {
            frm.set_df_property('custom_brand', 'options', []);
            frm.refresh_field('custom_brand');
            return;
        }

        frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "Item",
                name: frm.doc.item
            },
            callback: function(r) {
                if (r.message) {
                    const brand_table = r.message.custom_select_brand || [];
                    const brand_options = brand_table.map(row => row.brand_item);

                    // Set the options dynamically if custom_brand is a Select field
                    if (frm.fields_dict.custom_brand.df.fieldtype === "Select") {
                        frm.set_df_property("custom_brand", "options", [""].concat(brand_options));
                        frm.refresh_field("custom_brand");
                    }

                    // If it's a Link field, use a custom query
                    if (frm.fields_dict.custom_brand.df.fieldtype === "Link") {
                        frm.set_query("custom_brand", function() {
                            return {
                                filters: [
                                    ["Brand", "name", "in", brand_options]
                                ]
                            };
                        });
                    }
                }
            }
        });
    }
});

frappe.ui.form.on('Request for Quotation', {
    onload_post_render(frm) {
        if (frm.doc.__islocal) return;

        frm.dashboard.add_transactions = function () {
            frm.dashboard.add_transaction({
                items: [
                    {
                        label: 'Supplier Quotation',
                        doctype: 'Supplier Quotation',
                        on_click: () => {
                            const doc = frm.doc;

                            // Fetch supplier linked to current user
                            frappe.call({
                                method: "mohan_impex.user_roles.get_supplier_for_user",
                                args: { user: frappe.session.user },
                                callback(res) {
                                    const linked_supplier = res.message;

                                    if (!linked_supplier) {
                                        frappe.msgprint(__('You are not linked to any Supplier.'));
                                        return;
                                    }

                                    const suppliers = (doc.suppliers || []).map(row => row.supplier);

                                    if (!suppliers.includes(linked_supplier)) {
                                        frappe.msgprint(__('You are not one of the suppliers invited for this RFQ.'));
                                        return;
                                    }

                                    // Create dialog
                                    const dialog = new frappe.ui.Dialog({
                                        title: __("Create Supplier Quotation"),
                                        fields: [
                                            {
                                                fieldtype: "Link",
                                                label: __("Supplier"),
                                                fieldname: "supplier",
                                                options: "Supplier",
                                                reqd: 1
                                            }
                                        ],
                                        primary_action_label: __("Create"),
                                        primary_action(values) {
                                            frappe.call({
                                                method: "erpnext.buying.doctype.request_for_quotation.request_for_quotation.make_supplier_quotation",
                                                args: {
                                                    source_name: doc.name,
                                                    supplier: values.supplier
                                                },
                                                callback(res) {
                                                    if (!res.exc && res.message) {
                                                        frappe.set_route("Form", res.message.doctype, res.message.name);
                                                    }
                                                }
                                            });
                                            dialog.hide();
                                        }
                                    });

                                    dialog.show();

                                    // Set the supplier value after showing dialog
                                    dialog.set_value("supplier", linked_supplier);

                                    // Optional: Make it read-only
                                    dialog.fields_dict.supplier.df.read_only = 1;
                                    dialog.fields_dict.supplier.refresh();
                                }
                            });
                        }
                    }
                ]
            });
        };
    }
});

frappe.ui.form.on("Purchase Order Item", {
    item_code: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        let supplier = frm.doc.supplier;

        if (supplier && row.item_code) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Item",
                    name: row.item_code
                },
                callback: function(r) {
                    if (r.message && r.message.supplier_items) {
                        let matched = r.message.supplier_items.find(s => s.supplier === supplier);

                        if (matched) {
                            if (matched.custom_lead_time_in_days && frm.doc.transaction_date) {
                                let expected_date = frappe.datetime.add_days(
                                    frm.doc.transaction_date,
                                    matched.custom_lead_time_in_days
                                );
                                frappe.model.set_value(cdt, cdn, "expected_delivery_date", expected_date);
                            }

                            if (matched.supplier_part_no) {
                                frappe.model.set_value(cdt, cdn, "supplier_part_no", matched.supplier_part_no);
                            }
                        } else {
                            // Clear if no match
                            frappe.model.set_value(cdt, cdn, "expected_delivery_date", null);
                            frappe.model.set_value(cdt, cdn, "supplier_part_no", null);
                            frappe.msgprint(__('No matching supplier found in Item master for this item.'));
                        }
                    }
                }
            });
        }
    }
});

frappe.ui.form.on("Purchase Order", "validate", function(frm) {
	const today = frappe.datetime.get_today();

	if (frm.doc.transaction_date < today) {
		frappe.msgprint(__("Date cannot be in the past."));
		frappe.validated = false;
	}

	if (frm.doc.schedule_date && frm.doc.schedule_date < today) {
		frappe.msgprint(__("Required By Date cannot be in the past."));
		frappe.validated = false;
	}
});

