frappe.ui.form.on('Pick List Item', {
    item_code: function (frm, cdt, cdn) {
        const row = frappe.model.get_doc(cdt, cdn);

        // Clear brand if item_code is empty
        if (!row.item_code) {
            frappe.model.set_value(cdt, cdn, 'custom_select_brand', '');
            frm.fields_dict.locations.grid.update_docfield_property(
                'custom_select_brand',
                'options',
                ['']
            );
            return;
        }

        frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "Item",
                name: row.item_code
            },
            callback: function (r) {
                if (!r.exc && r.message) {
                    const brand_table = r.message.custom_select_brand || [];
                    const brand_options = brand_table.map(d => d.brand_item);

                    // Update brand select options for the *specific row*
                    frm.fields_dict.locations.grid.update_docfield_property(
                        'custom_select_brand',
                        'options',
                        [''].concat(brand_options)
                    );

                    // Clear selected brand value to avoid mismatch
                    frappe.model.set_value(cdt, cdn, 'custom_select_brand', '');

                    // Refresh only this row
                    frm.fields_dict.items.grid.refresh_row(row);
                }
            }
        });
    }
});

function set_item_code_filter(frm){
    frm.fields_dict["locations"].grid.get_field("item_code").get_query = function(doc, cdt, cdn) {
        let row = locals[cdt][cdn];
        return {
            filters: [
                ["has_variants", "=", 0],
                ["is_sales_item", "=", 1],
                ["variant_of", "=", row.item_template]
            ]
        };
    };
}

frappe.ui.form.on('Pick List', {
