frappe.ui.form.on('Purchase Receipt', {
    onload: function(frm) {
        frm.custom_tables_loaded = false;
    },

    refresh: function(frm) {
        // Load tables only once for new documents daiyan
        if (frm.is_new() && !frm.custom_tables_loaded) {

            // STEP 1: Get linked PO and set custom_type
            frappe.call({
                method: "mohan_impex.PR_Connection.get_linked_purchase_order",
                args: { purchase_receipt: frm.doc.name },
                callback: function (response) {
                    let purchase_order = response.message || '';
                    let custom_type = '';

                    if (purchase_order.startsWith("IMP")) {
                        custom_type = 'Import';
                    } else if (purchase_order.startsWith("DOM")) {
                        custom_type = 'Domestic';
                    }

                    frm.set_value('custom_type', custom_type || 'Domestic').then(() => {
                        // STEP 2: Load both tables
                        load_custom_documents(frm);
                        load_custom_item_details(frm);
                        frm.custom_tables_loaded = true;
                    });
                }
            });

            // Fallback if call doesn't return in time
            setTimeout(() => {
                if (!frm.custom_tables_loaded && frm.doc.custom_type) {
                    load_custom_documents(frm);
                    load_custom_item_details(frm);
                    frm.custom_tables_loaded = true;
                }
            }, 1000);
        }

        // Set item_code filter for each row
        frm.fields_dict["items"].grid.get_field("item_code").get_query = function (doc, cdt, cdn) {
            let row = locals[cdt][cdn];
            return {
                filters: [
                    ["has_variants", "=", 0],
                    ["is_sales_item", "=", 1],
                    ["variant_of", "=", row.item_template]
                ]
            };
        };
    },
});

