// Fetch shipping address details and set formatted address
function fetch_address_details(frm) {
    frappe.call({
        method: "frappe.client.get",
        args: {
            doctype: "Address",
            name: frm.doc.custom_company_shipping_address
        },
        callback: function (r) {
            if (r.message) {
                const addr = r.message;
                const formatted_address = [
                    addr.address_line1,
                    addr.address_line2,
                    addr.city,
                    addr.state,
                    addr.pincode,
                    addr.country
